/* Basscss Button Outline */

.button-outline {
  position: relative;
  z-index: 2;
  color: inherit;
  background-color: transparent;
  border-radius: 3px;
  border: 1px solid currentcolor;
  transition-duration: .1s;
  transition-timing-function: ease-out;
  transition-property: box-shadow, background-color;
}

.button-outline:before {
  content: '';
  width: 100%;
  height: 100%;
  display: block;
  position: absolute;
  z-index: -1;
  top: -1px;
  left: -1px;
  border: 1px solid transparent;
  background-color: currentcolor;
  border-radius: 3px;
  transition-duration: .1s;
  transition-timing-function: ease-out;
  transition-property: opacity;
  opacity: 0;
}

.button-outline:hover {
  box-shadow: none;
}

.button-outline:hover:before {
  opacity: .125;
}

.button-outline:focus {
  outline: none;
  border: 1px solid currentcolor;
  box-shadow: 0 0 3px 1px;
}

.button-outline:active,
.button-outline.is-active {
  box-shadow: inset 0 1px 5px 0, 0 0 1px;
}

.button-outline:disabled,
.button-outline.is-disabled {
  opacity: .5;
}



/* Basscss Button Transparent */

.button-transparent {
  position: relative;
  z-index: 2;
  color: inherit;
  background-color: transparent;
  border-radius: 0;
  border: 1px solid transparent;
  transition-duration: .1s;
  transition-timing-function: ease-out;
  transition-property: box-shadow;
}

.button-transparent:before {
  content: '';
  width: 100%;
  height: 100%;
  display: block;
  position: absolute;
  z-index: -1;
  top: -1px;
  left: -1px;
  border: 1px solid transparent;
  background-color: currentcolor;
  transition-duration: .1s;
  transition-timing-function: ease-out;
  transition-property: opacity;
  opacity: 0;
}

.button-transparent:hover {
  box-shadow: none;
}

.button-transparent:hover:before {
  opacity: .0625;
  opacity: .09375;
}

.button-transparent:focus {
  outline: none;
  border-color: transparent;
  box-shadow: 0 0 3px;
}

.button-transparent:active:before,
.button-transparent.is-active:before {
  opacity: .0625;
}

.button-transparent:disabled,
.button-transparent.is-disabled {
  opacity: .5;
}



/* Basscss Button Link */

.button-link {
  padding-right: 0;
  padding-left: 0;
  color: inherit;
  background-color: transparent;
  background-size: 100% .25em;
  background-repeat: repeat-x;
  background-position: 0 1.5rem;
  border-radius: 0;
}

.button-link:hover,
.button-link:focus {
  box-shadow: none;
  background-image: linear-gradient(transparent 50%, currentcolor 50%);
}

.button-link:active,
.button-link.is-active {
  box-shadow: inset 0 -.125em;
}

.button-link:focus {
  outline: none;
  border-color: transparent;
  box-shadow: 0 0 0 2px rgba(0, 0, 0, .25);
}

.button-link:disabled,
.button-link.is-disabled {
  opacity: .5;
}

