pre {
  padding: 1rem;
}

code {
  font-size: 87.5%;
}

svg {
  fill: currentColor;
}

.sf-display {
  font-family: 'San Francisco Display', 'Avenir Next', 'Helvetica Neue', Helvetica, sans-serif;
}

.sf-text {
  font-family: 'San Francisco Text', 'Avenir Next', 'Helvetica Neue', Helvetica, sans-serif;
}

.georgia {
  font-family: Georgia, serif;
}

.icon {
  display: inline-block;
  width: 1em;
  height: 1em;
}

.bright-blue {
  color: #0f8fff;
}

.bg-bright-blue {
  background-color: #0f8fff;
}

.bg-blue-alpha {
  background-color: rgba(0, 116, 217, .25);
}

.bg-red-alpha {
  background-color: rgba(255, 73, 22, .25);
}

.bg-green-alpha {
  background-color: rgba(46, 204, 64, .25);
}

.bg-yellow-alpha {
  background-color: rgba(255, 220, 0, .25);
}

/* Alpha backgrounds */

.border-thick {
  border-width: 2px;
}

